import React from 'react';
import { format } from 'date-fns';

const TaskForm = ({ taskForm, setTaskForm, onSubmit }) => {
  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit();
  };

  const handleClear = () => {
    setTaskForm({ title: '', date: format(new Date(), "yyyy-MM-dd'T'HH:mm") });
  };

  return (
    <form onSubmit={handleSubmit} className="task-form" style={{ marginTop: 16 }}>
      <div style={{ display: 'flex', flexDirection: 'column', gap: 8 }}>
        <input
          value={taskForm.title}
          onChange={(e) => setTaskForm(f => ({ ...f, title: e.target.value }))}
          placeholder="Название задачи"
          style={{ width: '100%' }}
        />
      </div>
      
      <div style={{ display: 'flex', flexDirection: 'column', gap: 8 }}>
        <input
          type="datetime-local"
          value={taskForm.date}
          onChange={(e) => setTaskForm(f => ({ ...f, date: e.target.value }))}
          style={{ width: '100%' }}
        />
      </div>
      
      <div style={{ display: 'flex', gap: 8, alignItems: 'center' }}>
        <button
          className="btn btn-primary"
          type="submit"
          style={{ flex: 1 }}
        >
          Добавить
        </button>
        <button
          className="btn"
          type="button"
          onClick={handleClear}
          style={{ flex: 1 }}
        >
          Очистить
        </button>
      </div>
    </form>
  );
};

export default TaskForm;
