import React, { useRef } from 'react';
import { save, STORAGE } from '../utils/storage';

const Settings = ({ isOpen, onClose, theme, onThemeChange, onFillSample, onReset }) => {
  const fileRef = useRef(null);

  const handleFileUpload = (e) => {
    const file = e.target.files && e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = () => {
      try {
        save(STORAGE.BG, reader.result);
        // Reload theme to apply background
        onThemeChange(theme);
      } catch {
        // Silently fail
      }
    };
    reader.readAsDataURL(file);
  };

  const handleUploadClick = () => {
    fileRef.current && fileRef.current.click();
  };

  const cycleTheme = () => {
    const themes = ['light', 'dark', 'glass', 'dark-glass'];
    const currentIndex = themes.indexOf(theme);
    const nextIndex = (currentIndex + 1) % themes.length;
    onThemeChange(themes[nextIndex]);
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()} style={{ maxWidth: '400px' }}>
        <div className="card" style={{ border: 'none', boxShadow: 'none', padding: '24px' }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 24 }}>
            <h3 style={{ margin: 0, fontSize: '1.5rem' }}>⚙️ Настройки</h3>
            <button 
              className="btn" 
              onClick={onClose}
              style={{ 
                width: '40px', 
                height: '40px', 
                borderRadius: '50%',
                padding: 0,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '1.25rem'
              }}
            >
              ✕
            </button>
          </div>

          <div style={{ display: 'flex', flexDirection: 'column', gap: 10 }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div>
                <div className="muted">Тема</div>
                <div style={{ fontWeight: 700 }}>{theme}</div>
              </div>
              <button
                className="btn"
                onClick={cycleTheme}
              >
                Сменить
              </button>
            </div>

            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div>
                <div className="muted">Демо</div>
                <div style={{ fontWeight: 700 }}>Заполнить пример</div>
              </div>
              <button className="btn btn-primary" onClick={onFillSample}>
                Пример
              </button>
            </div>

            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div>
                <div className="muted">Данные</div>
                <div style={{ fontWeight: 700 }}>Сброс</div>
              </div>
              <button
                className="btn"
                onClick={onReset}
                style={{ color: 'crimson' }}
              >
                Сброс
              </button>
            </div>

            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div className="muted">Фон</div>
              <div style={{ display: 'flex', gap: 8 }}>
                <button className="btn" onClick={handleUploadClick}>
                  Загрузить
                </button>
                <button 
                  className="btn" 
                  onClick={() => {
                    try {
                      save(STORAGE.BG, '');
                      onThemeChange(theme);
                    } catch {
                      // Silently fail
                    }
                  }}
                  style={{ color: 'var(--red)' }}
                >
                  Убрать
                </button>
                <input
                  ref={fileRef}
                  type="file"
                  accept="image/*"
                  style={{ display: 'none' }}
                  onChange={handleFileUpload}
                />
              </div>
            </div>

            {/* Demo of radio buttons */}
            <div style={{ 
              display: 'flex', 
              justifyContent: 'space-between', 
              alignItems: 'center',
              paddingTop: '8px',
              paddingBottom: '8px'
            }}>
              <div className="muted">Демо радио кнопок</div>
              <div style={{ 
                display: 'flex', 
                gap: 20, 
                alignItems: 'center',
                flexShrink: 0
              }}>
                <label style={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  gap: 10, 
                  cursor: 'pointer',
                  whiteSpace: 'nowrap'
                }}>
                  <input className="radio" type="radio" name="demo" value="option1" />
                  <span style={{ fontSize: '0.875rem', fontWeight: '500' }}>Опция 1</span>
                </label>
                <label style={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  gap: 10, 
                  cursor: 'pointer',
                  whiteSpace: 'nowrap'
                }}>
                  <input className="radio" type="radio" name="demo" value="option2" />
                  <span style={{ fontSize: '0.875rem', fontWeight: '500' }}>Опция 2</span>
                </label>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Settings;
