import React from 'react';

const HabitForm = ({ habitForm, setHabitForm, onSubmit }) => {
  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit();
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginTop: 16 }}>
      <div style={{ display: 'flex', gap: 12, flexWrap: 'wrap', alignItems: 'end' }}>
        <div style={{ flex: 1, minWidth: 200 }}>
          <input
            value={habitForm.title}
            onChange={(e) => setHabitForm(f => ({ ...f, title: e.target.value }))}
            placeholder="Название привычки"
            style={{ width: '100%' }}
          />
        </div>
        
        <div style={{ minWidth: 140 }}>
          <select
            value={habitForm.cadence}
            onChange={(e) => setHabitForm(f => ({ ...f, cadence: e.target.value }))}
            style={{ width: '100%' }}
          >
            <option value="daily">Ежедневно</option>
            <option value="weekly">Еженедельно</option>
          </select>
        </div>
        
        <button className="btn btn-primary" type="submit" style={{ minWidth: 120 }}>
          Добавить
        </button>
      </div>
    </form>
  );
};

export default HabitForm;
