import React from 'react';
import { format, isSameDay } from 'date-fns';
import { safeParse } from '../utils/dateUtils';

const TaskList = ({ tasks, selectedDate, onToggleTask, onRemoveTask }) => {
  const tasksForDate = tasks.filter(t => {
    const d = safeParse(t.datetime);
    return d ? isSameDay(d, selectedDate) : false;
  });

  const todayTasks = tasks.filter(t => {
    const d = safeParse(t.datetime);
    return d ? isSameDay(d, new Date()) : false;
  });

  return (
    <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 12, marginTop: 14 }}>
      <div>
        <h4>Задачи</h4>
        <div style={{ display: 'flex', flexDirection: 'column', gap: 8 }}>
          {tasksForDate.map(task => (
            <div
              key={task.id}
              style={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                padding: 10,
                borderRadius: 12,
                border: '1px solid rgba(0,0,0,0.04)'
              }}
            >
              <div style={{ display: 'flex', gap: 8, alignItems: 'center' }}>
                <input
                  className="checkbox"
                  type="checkbox"
                  checked={task.completed}
                  onChange={() => onToggleTask(task.id)}
                />
                <div>
                  <div style={{
                    fontWeight: 700,
                    textDecoration: task.completed ? 'line-through' : 'none'
                  }}>
                    {task.title}
                  </div>
                  <div className="muted">
                    {safeParse(task.datetime) ? format(safeParse(task.datetime), 'HH:mm') : '-'}
                  </div>
                </div>
              </div>
              <div>
                <button
                  className="btn"
                  onClick={() => onRemoveTask(task.id)}
                  style={{ color: 'crimson' }}
                >
                  Удалить
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      <div>
        <h4>Сегодня</h4>
        <div style={{ display: 'flex', flexDirection: 'column', gap: 8 }}>
          {todayTasks.map(task => (
            <div
              key={task.id}
              style={{
                display: 'flex',
                gap: 8,
                alignItems: 'center',
                padding: 10,
                borderRadius: 12,
                border: '1px solid rgba(0,0,0,0.04)'
              }}
            >
              <input
                className="checkbox"
                type="checkbox"
                checked={task.completed}
                onChange={() => onToggleTask(task.id)}
              />
              <div>
                <div style={{
                  fontWeight: 700,
                  textDecoration: task.completed ? 'line-through' : 'none'
                }}>
                  {task.title}
                </div>
                <div className="muted">
                  {safeParse(task.datetime) ? format(safeParse(task.datetime), 'HH:mm') : '-'}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TaskList;
