import React, { useRef, useState } from 'react';
import { save, STORAGE } from '../utils/storage';

const Settings = ({ isOpen, onClose, theme, onThemeChange, onFillSample, onReset }) => {
  const fileRef = useRef(null);
  const [uploadStatus, setUploadStatus] = useState('');
  const [isUploading, setIsUploading] = useState(false);

  const handleFileUpload = (e) => {
    const file = e.target.files && e.target.files[0];
    if (!file) return;

    // Reset status
    setUploadStatus('');
    setIsUploading(true);

    // Check file size (limit to 5MB for better performance)
    const maxSize = 5 * 1024 * 1024; // 5MB
    if (file.size > maxSize) {
      setUploadStatus('–û—à–∏–±–∫–∞: —Ñ–∞–π–ª —Å–ª–∏—à–∫–æ–º –±–æ–ª—å—à–æ–π (–º–∞–∫—Å–∏–º—É–º 5–ú–ë)');
      setIsUploading(false);
      return;
    }

    // Check file type
    const allowedTypes = ['image/jpeg', 'image/jpg', 'image/png', 'image/webp', 'image/gif'];
    if (!allowedTypes.includes(file.type)) {
      setUploadStatus('–û—à–∏–±–∫–∞: –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞—é—Ç—Å—è —Ç–æ–ª—å–∫–æ JPEG, PNG, WebP, GIF');
      setIsUploading(false);
      return;
    }

    const reader = new FileReader();

    reader.onload = () => {
      try {
        const result = reader.result;

        // Try to save the background
        save(STORAGE.BG, result);

        // Test if it was saved successfully
        const saved = localStorage.getItem(STORAGE.BG);
        if (!saved) {
          throw new Error('Failed to save to localStorage');
        }

        setUploadStatus('‚úÖ –§–æ–Ω —É—Å–ø–µ—à–Ω–æ –∑–∞–≥—Ä—É–∂–µ–Ω!');
        setIsUploading(false);

        // Reload theme to apply background
        setTimeout(() => {
          onThemeChange(theme);
          setUploadStatus('');
        }, 2000);

      } catch (error) {
        console.error('Background upload error:', error);
        setUploadStatus('–û—à–∏–±–∫–∞: –Ω–µ —É–¥–∞–ª–æ—Å—å —Å–æ—Ö—Ä–∞–Ω–∏—Ç—å —Ñ–æ–Ω');
        setIsUploading(false);
      }
    };

    reader.onerror = () => {
      setUploadStatus('–û—à–∏–±–∫–∞: –Ω–µ —É–¥–∞–ª–æ—Å—å –ø—Ä–æ—á–∏—Ç–∞—Ç—å —Ñ–∞–π–ª');
      setIsUploading(false);
    };

    // Read file as data URL
    reader.readAsDataURL(file);

    // Clear the input
    e.target.value = '';
  };

  const handleUploadClick = () => {
    if (!isUploading && fileRef.current) {
      fileRef.current.click();
    }
  };

  const handleRemoveBackground = () => {
    try {
      localStorage.removeItem(STORAGE.BG);
      setUploadStatus('‚úÖ –§–æ–Ω —É–¥–∞–ª–µ–Ω!');
      onThemeChange(theme);
      setTimeout(() => setUploadStatus(''), 2000);
    } catch (error) {
      setUploadStatus('–û—à–∏–±–∫–∞: –Ω–µ —É–¥–∞–ª–æ—Å—å —É–¥–∞–ª–∏—Ç—å —Ñ–æ–Ω');
    }
  };

  const cycleTheme = () => {
    const themes = ['light', 'dark', 'glass', 'dark-glass'];
    const currentIndex = themes.indexOf(theme);
    const nextIndex = (currentIndex + 1) % themes.length;
    onThemeChange(themes[nextIndex]);
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()} style={{ maxWidth: '400px' }}>
        <div className="card" style={{ border: 'none', boxShadow: 'none', padding: '24px' }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 24 }}>
            <h3 style={{ margin: 0, fontSize: '1.5rem' }}>‚öôÔ∏è –ù–∞—Å—Ç—Ä–æ–π–∫–∏</h3>
            <button 
              className="btn" 
              onClick={onClose}
              style={{ 
                width: '40px', 
                height: '40px', 
                borderRadius: '50%',
                padding: 0,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '1.25rem'
              }}
            >
              ‚úï
            </button>
          </div>

          <div style={{ display: 'flex', flexDirection: 'column', gap: 10 }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div>
                <div className="muted">–¢–µ–º–∞</div>
                <div style={{ fontWeight: 700 }}>{theme}</div>
              </div>
              <button
                className="btn"
                onClick={cycleTheme}
              >
                –°–º–µ–Ω–∏—Ç—å
              </button>
            </div>

            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div>
                <div className="muted">–î–µ–º–æ</div>
                <div style={{ fontWeight: 700 }}>–ó–∞–ø–æ–ª–Ω–∏—Ç—å –ø—Ä–∏–º–µ—Ä</div>
              </div>
              <button className="btn btn-primary" onClick={onFillSample}>
                –ü—Ä–∏–º–µ—Ä
              </button>
            </div>

            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div>
                <div className="muted">–î–∞–Ω–Ω—ã–µ</div>
                <div style={{ fontWeight: 700 }}>–°–±—Ä–æ—Å</div>
              </div>
              <button
                className="btn"
                onClick={onReset}
                style={{ color: 'crimson' }}
              >
                –°–±—Ä–æ—Å
              </button>
            </div>

            <div style={{ display: 'flex', flexDirection: 'column', gap: 8 }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <div>
                  <div className="muted">üá´üá© –§–æ–Ω</div>
                  <div style={{ fontWeight: 500, fontSize: '0.875rem' }}>–ó–∞–≥—Ä—É–∑–∏—Ç—å JPEG/PNG</div>
                </div>
                <div style={{ display: 'flex', gap: 8 }}>
                  <button 
                    className="btn btn-orange" 
                    onClick={handleUploadClick}
                    disabled={isUploading}
                    style={{ 
                      minWidth: '90px',
                      fontSize: '0.85rem'
                    }}
                  >
                    {isUploading ? '–ó–∞–≥—Ä—É–∑–∫–∞...' : 'üìÅ –ó–∞–≥—Ä—É–∑–∏—Ç—å'}
                  </button>
                  <button 
                    className="btn" 
                    onClick={handleRemoveBackground}
                    disabled={isUploading}
                    style={{ 
                      color: 'var(--red)',
                      minWidth: '70px',
                      fontSize: '0.85rem'
                    }}
                  >
                    üóëÔ∏è –£–±—Ä–∞—Ç—å
                  </button>
                </div>
              </div>

              {/* Upload status */}
              {uploadStatus && (
                <div style={{ 
                  padding: '8px 12px',
                  borderRadius: '8px',
                  fontSize: '0.8rem',
                  fontWeight: '500',
                  backgroundColor: uploadStatus.includes('–û—à–∏–±–∫–∞') 
                    ? 'rgba(255, 59, 48, 0.1)' 
                    : 'rgba(52, 199, 89, 0.1)',
                  color: uploadStatus.includes('–û—à–∏–±–∫–∞') 
                    ? 'var(--red)' 
                    : 'var(--green)',
                  border: `1px solid ${uploadStatus.includes('–û—à–∏–±–∫–∞') 
                    ? 'rgba(255, 59, 48, 0.2)' 
                    : 'rgba(52, 199, 89, 0.2)'}`
                }}>
                  {uploadStatus}
                </div>
              )}

              <input
                ref={fileRef}
                type="file"
                accept="image/jpeg,image/jpg,image/png,image/webp,image/gif"
                style={{ display: 'none' }}
                onChange={handleFileUpload}
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Settings;
