import React, { useRef, useState } from 'react';
import { save, STORAGE } from '../utils/storage';

const Settings = ({ isOpen, onClose, theme, onThemeChange, onFillSample, onReset }) => {
  const fileRef = useRef(null);
  const [uploadStatus, setUploadStatus] = useState('');
  const [isUploading, setIsUploading] = useState(false);

  const handleFileUpload = (e) => {
    const file = e.target.files && e.target.files[0];
    if (!file) return;

    // Reset status
    setUploadStatus('');
    setIsUploading(true);

    // Check file size (limit to 5MB for better performance)
    const maxSize = 5 * 1024 * 1024; // 5MB
    if (file.size > maxSize) {
      setUploadStatus('Ошибка: файл слишком большой (максимум 5МБ)');
      setIsUploading(false);
      return;
    }

    // Check file type
    const allowedTypes = ['image/jpeg', 'image/jpg', 'image/png', 'image/webp', 'image/gif'];
    if (!allowedTypes.includes(file.type)) {
      setUploadStatus('Ошибка: поддерживаются только JPEG, PNG, WebP, GIF');
      setIsUploading(false);
      return;
    }

    const reader = new FileReader();

    reader.onload = () => {
      try {
        const result = reader.result;

        // Try to save the background
        save(STORAGE.BG, result);

        // Test if it was saved successfully
        const saved = localStorage.getItem(STORAGE.BG);
        if (!saved) {
          throw new Error('Failed to save to localStorage');
        }

        setUploadStatus('✅ Фон успешно загружен!');
        setIsUploading(false);

        // Reload theme to apply background
        setTimeout(() => {
          onThemeChange(theme);
          setUploadStatus('');
        }, 2000);

      } catch (error) {
        console.error('Background upload error:', error);
        setUploadStatus('Ошибка: не удалось сохранить фон');
        setIsUploading(false);
      }
    };

    reader.onerror = () => {
      setUploadStatus('Ошибка: не удалось прочитать файл');
      setIsUploading(false);
    };

    // Read file as data URL
    reader.readAsDataURL(file);

    // Clear the input
    e.target.value = '';
  };

  const handleUploadClick = () => {
    if (!isUploading && fileRef.current) {
      fileRef.current.click();
    }
  };

  const handleRemoveBackground = () => {
    try {
      localStorage.removeItem(STORAGE.BG);
      setUploadStatus('✅ Фон удален!');
      onThemeChange(theme);
      setTimeout(() => setUploadStatus(''), 2000);
    } catch (error) {
      setUploadStatus('Ошибка: не удалось удалить фон');
    }
  };

  const cycleTheme = () => {
    const themes = ['light', 'dark', 'glass', 'dark-glass'];
    const currentIndex = themes.indexOf(theme);
    const nextIndex = (currentIndex + 1) % themes.length;
    onThemeChange(themes[nextIndex]);
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()} style={{ maxWidth: '400px' }}>
        <div className="card" style={{ border: 'none', boxShadow: 'none', padding: '24px' }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 24 }}>
            <h3 style={{ margin: 0, fontSize: '1.5rem' }}>⚙️ Настройки</h3>
            <button 
              className="btn" 
              onClick={onClose}
              style={{ 
                width: '40px', 
                height: '40px', 
                borderRadius: '50%',
                padding: 0,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '1.25rem'
              }}
            >
              ✕
            </button>
          </div>

          <div style={{ display: 'flex', flexDirection: 'column', gap: 10 }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div>
                <div className="muted">Тема</div>
                <div style={{ fontWeight: 700 }}>{theme}</div>
              </div>
              <button
                className="btn"
                onClick={cycleTheme}
              >
                Сменить
              </button>
            </div>

            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div>
                <div className="muted">Демо</div>
                <div style={{ fontWeight: 700 }}>Заполнить пример</div>
              </div>
              <button className="btn btn-primary" onClick={onFillSample}>
                Пример
              </button>
            </div>

            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div>
                <div className="muted">Данные</div>
                <div style={{ fontWeight: 700 }}>Сброс</div>
              </div>
              <button
                className="btn"
                onClick={onReset}
                style={{ color: 'crimson' }}
              >
                Сброс
              </button>
            </div>

            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div className="muted">Фон</div>
              <div style={{ display: 'flex', gap: 8 }}>
                <button className="btn" onClick={handleUploadClick}>
                  Загрузить
                </button>
                <button 
                  className="btn" 
                  onClick={() => {
                    try {
                      save(STORAGE.BG, '');
                      onThemeChange(theme);
                    } catch {
                      // Silently fail
                    }
                  }}
                  style={{ color: 'var(--red)' }}
                >
                  Убрать
                </button>
                <input
                  ref={fileRef}
                  type="file"
                  accept="image/*"
                  style={{ display: 'none' }}
                  onChange={handleFileUpload}
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Settings;
