import React, { useState } from 'react';
import { BarChart, Bar, AreaChart, Area, LineChart, Line, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend } from 'recharts';
import { format, subDays, subMonths, startOfMonth, endOfMonth, eachDayOfInterval, eachMonthOfInterval } from 'date-fns';
import { ru } from 'date-fns/locale';

const Statistics = ({ isOpen, onClose, tasks, habits, pomodoros }) => {
  const [period, setPeriod] = useState('month'); // 'month' or 'year'

  if (!isOpen) return null;

  // Generate data for the selected period
  const generateData = () => {
    const today = new Date();
    
    if (period === 'month') {
      const startDate = startOfMonth(today);
      const endDate = endOfMonth(today);
      const days = eachDayOfInterval({ start: startDate, end: endDate });
      
      return days.map(day => {
        const dayStr = format(day, 'yyyy-MM-dd');
        const dayTasks = tasks.filter(task => {
          const taskDate = new Date(task.datetime);
          return taskDate.toDateString() === day.toDateString();
        });
        
        const completedTasks = dayTasks.filter(task => task.completed).length;
        const totalTasks = dayTasks.length;
        
        const dayHabits = habits.filter(habit => {
          const completedDates = habit.completedDates || [];
          return completedDates.includes(dayStr);
        }).length;
        
        const dayPomodoros = pomodoros?.filter(p => {
          const pomodoroDate = new Date(p.date);
          return pomodoroDate.toDateString() === day.toDateString();
        }).length || 0;
        
        return {
          date: format(day, 'dd MMM', { locale: ru }),
          fullDate: dayStr,
          tasks: totalTasks,
          completedTasks,
          completionRate: totalTasks > 0 ? Math.round((completedTasks / totalTasks) * 100) : 0,
          habits: dayHabits,
          pomodoros: dayPomodoros
        };
      });
    } else {
      // Year data - monthly aggregation
      const startDate = new Date(today.getFullYear(), 0, 1);
      const endDate = today;
      const months = eachMonthOfInterval({ start: startDate, end: endDate });
      
      return months.map(month => {
        const monthStart = startOfMonth(month);
        const monthEnd = endOfMonth(month);
        
        const monthTasks = tasks.filter(task => {
          const taskDate = new Date(task.datetime);
          return taskDate >= monthStart && taskDate <= monthEnd;
        });
        
        const completedTasks = monthTasks.filter(task => task.completed).length;
        const totalTasks = monthTasks.length;
        
        const monthHabits = habits.reduce((total, habit) => {
          const completedDates = habit.completedDates || [];
          const monthHabitCount = completedDates.filter(date => {
            const habitDate = new Date(date);
            return habitDate >= monthStart && habitDate <= monthEnd;
          }).length;
          return total + monthHabitCount;
        }, 0);
        
        const monthPomodoros = pomodoros?.filter(p => {
          const pomodoroDate = new Date(p.date);
          return pomodoroDate >= monthStart && pomodoroDate <= monthEnd;
        }).length || 0;
        
        return {
          date: format(month, 'MMM yyyy', { locale: ru }),
          fullDate: format(month, 'yyyy-MM'),
          tasks: totalTasks,
          completedTasks,
          completionRate: totalTasks > 0 ? Math.round((completedTasks / totalTasks) * 100) : 0,
          habits: monthHabits,
          pomodoros: monthPomodoros
        };
      });
    }
  };

  const data = generateData();

  // Calculate overall statistics
  const totalTasks = tasks.length;
  const completedTasks = tasks.filter(task => task.completed).length;
  const totalHabits = habits.length;
  const totalPomodoros = pomodoros?.length || 0;
  
  const totalHabitCompletions = habits.reduce((total, habit) => {
    return total + (habit.completedDates?.length || 0);
  }, 0);

  // Custom tooltip
  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      return (
        <div style={{
          backgroundColor: 'var(--card)',
          border: '1px solid var(--glass-border, rgba(0, 0, 0, 0.1))',
          borderRadius: '12px',
          padding: '12px',
          boxShadow: '0 8px 24px rgba(11, 18, 32, 0.15)',
          backdropFilter: 'blur(10px)',
          fontFamily: 'var(--font-family)'
        }}>
          <p style={{ 
            margin: '0 0 8px 0', 
            fontWeight: '600', 
            color: 'var(--text)',
            fontSize: '0.875rem'
          }}>
            {label}
          </p>
          {payload.map((entry, index) => (
            <p key={index} style={{ 
              margin: '4px 0', 
              color: 'var(--muted)',
              fontSize: '0.8rem'
            }}>
              <span style={{ color: entry.color, fontWeight: '500' }}>
                {entry.name}:
              </span> {entry.value}
            </p>
          ))}
        </div>
      );
    }
    return null;
  };

  return (
    <>
      <div className="settings-overlay" onClick={onClose} />
      <div className="settings-panel" style={{ 
        width: '90vw', 
        maxWidth: '1200px',
        left: '50%', 
        top: '50%', 
        transform: 'translate(-50%, -50%)',
        maxHeight: '90vh',
        overflow: 'hidden',
        borderRadius: '20px'
      }}>
        <div className="card" style={{ 
          height: '100%', 
          display: 'flex', 
          flexDirection: 'column',
          borderRadius: '20px',
          padding: '24px'
        }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 24 }}>
            <h2 style={{ margin: 0, fontSize: '1.75rem' }}>📊 Подробная статистика</h2>
            <button 
              className="btn" 
              onClick={onClose}
              style={{ 
                width: '40px', 
                height: '40px', 
                borderRadius: '50%',
                padding: 0,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '1.25rem'
              }}
            >
              ✕
            </button>
          </div>

          {/* Period selector */}
          <div style={{ display: 'flex', gap: 12, marginBottom: 24 }}>
            <button 
              className={`btn ${period === 'month' ? 'btn-primary' : ''}`}
              onClick={() => setPeriod('month')}
              style={{ 
                padding: '10px 20px',
                borderRadius: '12px',
                fontWeight: period === 'month' ? '600' : '500'
              }}
            >
              Месяц
            </button>
            <button 
              className={`btn ${period === 'year' ? 'btn-primary' : ''}`}
              onClick={() => setPeriod('year')}
              style={{ 
                padding: '10px 20px',
                borderRadius: '12px',
                fontWeight: period === 'year' ? '600' : '500'
              }}
            >
              Год
            </button>
          </div>

          {/* Overall stats cards */}
          <div style={{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', 
            gap: 16, 
            marginBottom: 32 
          }}>
            <div className="chart-card" style={{ textAlign: 'center', padding: '20px' }}>
              <div style={{ fontSize: '2.5rem', fontWeight: '700', color: 'var(--accent)', marginBottom: '8px' }}>
                {completedTasks}/{totalTasks}
              </div>
              <div className="muted" style={{ fontSize: '0.9rem' }}>Задач выполнено</div>
            </div>
            
            <div className="chart-card" style={{ textAlign: 'center', padding: '20px' }}>
              <div style={{ fontSize: '2.5rem', fontWeight: '700', color: 'var(--green)', marginBottom: '8px' }}>
                {totalHabitCompletions}
              </div>
              <div className="muted" style={{ fontSize: '0.9rem' }}>Привычек выполнено</div>
            </div>
            
            <div className="chart-card" style={{ textAlign: 'center', padding: '20px' }}>
              <div style={{ fontSize: '2.5rem', fontWeight: '700', color: 'var(--accent-2)', marginBottom: '8px' }}>
                {totalPomodoros}
              </div>
              <div className="muted" style={{ fontSize: '0.9rem' }}>Помидорок всего</div>
            </div>
            
            <div className="chart-card" style={{ textAlign: 'center', padding: '20px' }}>
              <div style={{ fontSize: '2.5rem', fontWeight: '700', color: 'var(--yellow)', marginBottom: '8px' }}>
                {totalTasks > 0 ? Math.round((completedTasks / totalTasks) * 100) : 0}%
              </div>
              <div className="muted" style={{ fontSize: '0.9rem' }}>Общий прогресс</div>
            </div>
          </div>

          {/* Scrollable content */}
          <div style={{ 
            flex: 1, 
            overflow: 'auto',
            overflowX: 'hidden',
            paddingRight: '8px', // Space for scrollbar
            maxHeight: 'calc(90vh - 300px)' // Ensure it doesn't exceed viewport
          }}>
            {/* Charts */}
            <div style={{ display: 'grid', gap: 24, gridTemplateColumns: '1fr 1fr', marginBottom: 24 }}>
              {/* Tasks completion rate */}
              <div className="chart-card">
                <div className="chart-title">
                  📈 Процент выполнения задач
                </div>
                <ResponsiveContainer width="100%" height={200}>
                  <AreaChart data={data}>
                    <CartesianGrid strokeDasharray="3 3" stroke="var(--input-border)" opacity={0.3} />
                    <XAxis 
                      dataKey="date" 
                      tick={{ fontSize: 12, fill: 'var(--muted)' }}
                    />
                    <YAxis 
                      tick={{ fontSize: 12, fill: 'var(--muted)' }}
                      domain={[0, 100]}
                    />
                    <Tooltip content={<CustomTooltip />} />
                    <Area 
                      type="monotone" 
                      dataKey="completionRate" 
                      stroke="var(--accent)" 
                      fill="url(#completionGradient)"
                      name="Процент выполнения"
                    />
                    <defs>
                      <linearGradient id="completionGradient" x1="0" y1="0" x2="0" y2="1">
                        <stop offset="0%" stopColor="var(--accent)" stopOpacity={0.3} />
                        <stop offset="100%" stopColor="var(--accent)" stopOpacity={0.1} />
                      </linearGradient>
                    </defs>
                  </AreaChart>
                </ResponsiveContainer>
              </div>

              {/* Pomodoros */}
              <div className="chart-card">
                <div className="chart-title">
                  🍅 Помидорки
                </div>
                <ResponsiveContainer width="100%" height={200}>
                  <BarChart data={data}>
                    <CartesianGrid strokeDasharray="3 3" stroke="var(--input-border)" opacity={0.3} />
                    <XAxis 
                      dataKey="date" 
                      tick={{ fontSize: 12, fill: 'var(--muted)' }}
                    />
                    <YAxis 
                      tick={{ fontSize: 12, fill: 'var(--muted)' }}
                    />
                    <Tooltip content={<CustomTooltip />} />
                    <Bar 
                      dataKey="pomodoros" 
                      fill="var(--accent-2)" 
                      radius={[4, 4, 0, 0]}
                      name="Помидорки"
                    />
                  </BarChart>
                </ResponsiveContainer>
              </div>

              {/* Tasks vs Completed */}
              <div className="chart-card">
                <div className="chart-title">
                  ✅ Задачи: выполнено vs всего
                </div>
                <ResponsiveContainer width="100%" height={200}>
                  <BarChart data={data}>
                    <CartesianGrid strokeDasharray="3 3" stroke="var(--input-border)" opacity={0.3} />
                    <XAxis 
                      dataKey="date" 
                      tick={{ fontSize: 12, fill: 'var(--muted)' }}
                    />
                    <YAxis 
                      tick={{ fontSize: 12, fill: 'var(--muted)' }}
                    />
                    <Tooltip content={<CustomTooltip />} />
                    <Bar 
                      dataKey="tasks" 
                      fill="var(--muted)" 
                      radius={[4, 4, 0, 0]}
                      name="Всего задач"
                    />
                    <Bar 
                      dataKey="completedTasks" 
                      fill="var(--green)" 
                      radius={[4, 4, 0, 0]}
                      name="Выполнено"
                    />
                  </BarChart>
                </ResponsiveContainer>
              </div>

              {/* Habits activity */}
              <div className="chart-card">
                <div className="chart-title">
                  🔄 Активность привычек
                </div>
                <ResponsiveContainer width="100%" height={200}>
                  <LineChart data={data}>
                    <CartesianGrid strokeDasharray="3 3" stroke="var(--input-border)" opacity={0.3} />
                    <XAxis 
                      dataKey="date" 
                      tick={{ fontSize: 12, fill: 'var(--muted)' }}
                    />
                    <YAxis 
                      tick={{ fontSize: 12, fill: 'var(--muted)' }}
                    />
                    <Tooltip content={<CustomTooltip />} />
                    <Line 
                      type="monotone" 
                      dataKey="habits" 
                      stroke="var(--green)" 
                      strokeWidth={3}
                      dot={{ fill: 'var(--green)', strokeWidth: 2, r: 4 }}
                      name="Привычки"
                    />
                  </LineChart>
                </ResponsiveContainer>
              </div>
            </div>

            {/* Summary table */}
            <div className="chart-card">
              <div className="chart-title">
                📋 Детальная таблица
              </div>
              <div style={{ overflowX: 'auto' }}>
                <table style={{ 
                  width: '100%', 
                  borderCollapse: 'collapse',
                  fontSize: '0.875rem'
                }}>
                  <thead>
                    <tr style={{ borderBottom: '1px solid var(--input-border)' }}>
                      <th style={{ padding: '16px 12px', textAlign: 'left', fontWeight: '600', color: 'var(--text)' }}>Дата</th>
                      <th style={{ padding: '16px 12px', textAlign: 'center', fontWeight: '600', color: 'var(--text)' }}>Задач</th>
                      <th style={{ padding: '16px 12px', textAlign: 'center', fontWeight: '600', color: 'var(--text)' }}>Выполнено</th>
                      <th style={{ padding: '16px 12px', textAlign: 'center', fontWeight: '600', color: 'var(--text)' }}>Процент</th>
                      <th style={{ padding: '16px 12px', textAlign: 'center', fontWeight: '600', color: 'var(--text)' }}>Привычки</th>
                      <th style={{ padding: '16px 12px', textAlign: 'center', fontWeight: '600', color: 'var(--text)' }}>Помидорки</th>
                    </tr>
                  </thead>
                  <tbody>
                    {data.map((row, index) => (
                      <tr key={index} style={{ 
                        borderBottom: '1px solid var(--input-border)',
                        transition: 'background-color 0.2s ease'
                      }}>
                        <td style={{ padding: '16px 12px', fontWeight: '500', color: 'var(--text)' }}>{row.date}</td>
                        <td style={{ padding: '16px 12px', textAlign: 'center', color: 'var(--muted)' }}>{row.tasks}</td>
                        <td style={{ padding: '16px 12px', textAlign: 'center', color: 'var(--green)', fontWeight: '500' }}>{row.completedTasks}</td>
                        <td style={{ padding: '16px 12px', textAlign: 'center', color: 'var(--accent)', fontWeight: '500' }}>{row.completionRate}%</td>
                        <td style={{ padding: '16px 12px', textAlign: 'center', color: 'var(--muted)' }}>{row.habits}</td>
                        <td style={{ padding: '16px 12px', textAlign: 'center', color: 'var(--accent-2)', fontWeight: '500' }}>{row.pomodoros}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Statistics;
