import React from 'react';
import Calendar from 'react-calendar';
import { format, isSameDay } from 'date-fns';
import { safeParse } from '../utils/dateUtils';
import 'react-calendar/dist/Calendar.css';

const CalendarComponent = ({ selectedDate, onDateChange, tasks }) => {
  // Calendar tile className for background colors only
  const tileClassName = ({ date, view }) => {
    if (view !== 'month') return '';
    
    const tasksDone = tasks.filter(t => {
      const d = safeParse(t.datetime);
      return d ? isSameDay(d, date) && t.completed : false;
    }).length;
    
    const tasksTotal = tasks.filter(t => {
      const d = safeParse(t.datetime);
      return d ? isSameDay(d, date) : false;
    }).length;
    
    if (!tasksTotal) return 'cal-empty';
    
    const percent = Math.round((tasksDone / tasksTotal) * 100);
    return percent >= 85 ? 'cal-green' : percent >= 50 ? 'cal-yellow' : 'cal-red';
  };

  return (
    <Calendar
      onChange={onDateChange}
      value={selectedDate}
      tileClassName={tileClassName}
    />
  );
};

export default CalendarComponent;
