import React from 'react';

const HabitForm = ({ habitForm, setHabitForm, onSubmit }) => {
  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit();
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: '12px' }}>
      <div style={{ display: 'flex', gap: '8px', alignItems: 'flex-end' }}>
        <div style={{ flex: 1 }}>
          <label 
            htmlFor="habit-title" 
            style={{ 
              display: 'block', 
              marginBottom: '4px', 
              fontSize: '0.8rem', 
              fontWeight: '500',
              color: 'var(--text)'
            }}
          >
            Название привычки
          </label>
          <input
            id="habit-title"
            type="text"
            value={habitForm.title}
            onChange={(e) => setHabitForm({ ...habitForm, title: e.target.value })}
            placeholder="Например: Утренняя зарядка"
            style={{
              width: '100%',
              padding: '8px 12px',
              border: '1px solid var(--border)',
              borderRadius: '12px',
              fontSize: '0.9rem',
              background: 'var(--surface)',
              backdropFilter: 'var(--backdrop-blur)',
              WebkitBackdropFilter: 'var(--backdrop-blur)',
              color: 'var(--text)',
              boxShadow: 'var(--shadow)',
              transition: 'all var(--transition-normal)',
              outline: 'none'
            }}
            onFocus={(e) => {
              e.target.style.borderColor = 'var(--orange)';
              e.target.style.boxShadow = '0 0 0 3px var(--orange-glow), var(--shadow-hover)';
              e.target.style.background = 'var(--surface-hover)';
            }}
            onBlur={(e) => {
              e.target.style.borderColor = 'var(--border)';
              e.target.style.boxShadow = 'var(--shadow)';
              e.target.style.background = 'var(--surface)';
            }}
          />
        </div>
        
        <div>
          <label 
            htmlFor="habit-cadence" 
            style={{ 
              display: 'block', 
              marginBottom: '4px', 
              fontSize: '0.8rem', 
              fontWeight: '500',
              color: 'var(--text)'
            }}
          >
            Частота
          </label>
          <select
            id="habit-cadence"
            value={habitForm.cadence}
            onChange={(e) => setHabitForm({ ...habitForm, cadence: e.target.value })}
            style={{
              padding: '8px 12px',
              paddingRight: '32px',
              border: '1px solid var(--border)',
              borderRadius: '12px',
              fontSize: '0.9rem',
              background: 'var(--surface)',
              backdropFilter: 'var(--backdrop-blur)',
              WebkitBackdropFilter: 'var(--backdrop-blur)',
              color: 'var(--text)',
              cursor: 'pointer',
              appearance: 'none',
              WebkitAppearance: 'none',
              MozAppearance: 'none',
              backgroundImage: `url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' fill='none' viewBox='0 0 20 20'%3e%3cpath stroke='%23${getComputedStyle(document.documentElement).getPropertyValue('--text-secondary').replace('#', '')}' stroke-linecap='round' stroke-linejoin='round' stroke-width='1.5' d='m6 8 4 4 4-4'/%3e%3c/svg%3e")`,
              backgroundPosition: 'right 8px center',
              backgroundRepeat: 'no-repeat',
              backgroundSize: '16px',
              boxShadow: 'var(--shadow)',
              transition: 'all var(--transition-normal)',
              minHeight: '36px'
            }}
            onFocus={(e) => {
              e.target.style.borderColor = 'var(--orange)';
              e.target.style.boxShadow = '0 0 0 3px var(--orange-glow), var(--shadow-hover)';
              e.target.style.background = 'var(--surface-hover)';
            }}
            onBlur={(e) => {
              e.target.style.borderColor = 'var(--border)';
              e.target.style.boxShadow = 'var(--shadow)';
              e.target.style.background = 'var(--surface)';
            }}
            onMouseEnter={(e) => {
              e.target.style.background = 'var(--surface-hover)';
              e.target.style.borderColor = 'var(--border-hover)';
            }}
            onMouseLeave={(e) => {
              if (document.activeElement !== e.target) {
                e.target.style.background = 'var(--surface)';
                e.target.style.borderColor = 'var(--border)';
              }
            }}
          >
            <option value="daily" style={{ 
              background: 'var(--surface)', 
              color: 'var(--text)',
              padding: '8px 12px',
              fontSize: '0.9rem'
            }}>Ежедневно</option>
            <option value="weekly" style={{ 
              background: 'var(--surface)', 
              color: 'var(--text)',
              padding: '8px 12px',
              fontSize: '0.9rem'
            }}>Еженедельно</option>
            <option value="monthly" style={{ 
              background: 'var(--surface)', 
              color: 'var(--text)',
              padding: '8px 12px',
              fontSize: '0.9rem'
            }}>Ежемесячно</option>
          </select>
        </div>
        
        <button
          type="submit"
          className="btn btn-primary"
          disabled={!habitForm.title.trim()}
          style={{
            padding: '8px 16px',
            borderRadius: '12px',
            fontSize: '0.9rem',
            fontWeight: '600',
            cursor: habitForm.title.trim() ? 'pointer' : 'not-allowed',
            opacity: habitForm.title.trim() ? 1 : 0.6,
            transition: 'all 0.2s ease',
            whiteSpace: 'nowrap'
          }}
        >
          Добавить
        </button>
      </div>
    </form>
  );
};

export default HabitForm;
