import React from 'react';
import { format } from 'date-fns';

const TaskForm = ({ taskForm, setTaskForm, onSubmit }) => {
  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit();
  };

  const handleClear = () => {
    setTaskForm({ title: '', date: format(new Date(), "yyyy-MM-dd'T'HH:mm") });
  };

  return (
    <form onSubmit={handleSubmit} className="task-form" style={{ marginTop: 12 }}>
      <input
        value={taskForm.title}
        onChange={(e) => setTaskForm(f => ({ ...f, title: e.target.value }))}
        placeholder="Название"
        style={{
          padding: 10,
          borderRadius: 10,
          border: '1px solid rgba(0,0,0,0.06)',
          minWidth: 80
        }}
      />
      <input
        type="datetime-local"
        value={taskForm.date}
        onChange={(e) => setTaskForm(f => ({ ...f, date: e.target.value }))}
        style={{
          padding: 10,
          borderRadius: 10,
          border: '1px solid rgba(0,0,0,0.06)',
          minWidth: 140
        }}
      />
      <div style={{
        display: 'flex',
        gap: 8,
        alignItems: 'center',
        justifyContent: 'flex-end',
        minWidth: 0
      }}>
        <button
          className="btn btn-primary"
          type="submit"
          style={{ whiteSpace: 'nowrap' }}
        >
          Добавить
        </button>
        <button
          className="btn"
          type="button"
          onClick={handleClear}
          style={{ whiteSpace: 'nowrap' }}
        >
          Очистить
        </button>
      </div>
    </form>
  );
};

export default TaskForm;
