import React from 'react';

const HabitForm = ({ habitForm, setHabitForm, onSubmit }) => {
  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit();
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: 'flex', gap: 8, marginTop: 10, flexWrap: 'wrap' }}>
      <input
        value={habitForm.title}
        onChange={(e) => setHabitForm(f => ({ ...f, title: e.target.value }))}
        placeholder="Новая привычка"
        style={{ padding: 8, borderRadius: 8, minWidth: 140 }}
      />
      <select
        value={habitForm.cadence}
        onChange={(e) => setHabitForm(f => ({ ...f, cadence: e.target.value }))}
        style={{ padding: 8, borderRadius: 8 }}
      >
        <option value="daily">Ежедневно</option>
        <option value="weekly">Еженедельно</option>
      </select>
      <button className="btn btn-primary" type="submit">
        Добавить
      </button>
    </form>
  );
};

export default HabitForm;
