import { useEffect } from 'react';
import { load, STORAGE } from '../utils/storage';

export const useTheme = (theme) => {
  useEffect(() => {
    const root = document.documentElement;

    // Remove all theme classes
    root.classList.remove('dark', 'glass', 'dark-glass');

    // Add current theme class
    if (theme === 'dark') {
      root.classList.add('dark');
      root.style.setProperty('--bg', '#071127');
      root.style.setProperty('--card', '#0f2433');
      root.style.setProperty('--text', '#e6f2ff');
      root.style.setProperty('--muted', '#9fb0c8');
      root.style.setProperty('--accent', '#f59e0b');
      root.style.setProperty('--accent-2', '#fb7185');
      root.style.setProperty('--green', '#10b981');
      root.style.setProperty('--yellow', '#f59e0b');
      root.style.setProperty('--red', '#ef4444');
    } else if (theme === 'glass') {
      root.classList.add('glass');
      root.style.setProperty('--bg', 'linear-gradient(135deg,#faf6ed,#fffaf0)');
      root.style.setProperty('--card', 'rgba(255,255,255,0.28)');
      root.style.setProperty('--text', '#0b1220');
      root.style.setProperty('--muted', '#61656a');
      root.style.setProperty('--accent', '#f59e0b');
      root.style.setProperty('--accent-2', '#fb7185');
      root.style.setProperty('--green', '#10b981');
      root.style.setProperty('--yellow', '#f59e0b');
      root.style.setProperty('--red', '#ef4444');
      root.style.setProperty('--glass-border', 'rgba(255,255,255,0.45)');
    } else if (theme === 'dark-glass') {
      root.classList.add('dark-glass');
      root.style.setProperty('--bg', 'linear-gradient(135deg, #0a0f1a, #1a1f2e)');
      root.style.setProperty('--card', 'rgba(15, 36, 51, 0.6)');
      root.style.setProperty('--text', '#e6f2ff');
      root.style.setProperty('--muted', '#9fb0c8');
      root.style.setProperty('--accent', '#f59e0b');
      root.style.setProperty('--accent-2', '#fb7185');
      root.style.setProperty('--green', '#10b981');
      root.style.setProperty('--yellow', '#f59e0b');
      root.style.setProperty('--red', '#ef4444');
      root.style.setProperty('--glass-border', 'rgba(255, 255, 255, 0.1)');
    } else {
      // Light theme (default)
      root.style.setProperty('--bg', '#fff7ed');
      root.style.setProperty('--card', '#ffffff');
      root.style.setProperty('--text', '#1f2937');
      root.style.setProperty('--muted', '#6b7280');
      root.style.setProperty('--accent', '#f59e0b');
      root.style.setProperty('--accent-2', '#fb7185');
      root.style.setProperty('--green', '#10b981');
      root.style.setProperty('--yellow', '#f59e0b');
      root.style.setProperty('--red', '#ef4444');
    }

    // Load custom background with better error handling
    try {
      const customBg = load(STORAGE.BG, null);

      if (customBg && customBg.trim() !== '') {
        root.style.setProperty('--custom-bg', `url("${customBg}")`);
      } else {
        root.style.setProperty('--custom-bg', 'none');
      }
    } catch (error) {
      console.error('Error loading custom background:', error);
      root.style.setProperty('--custom-bg', 'none');
    }
  }, [theme]);
};
