import React from 'react';
import CalendarComponent from './Calendar';

const Sidebar = ({ selectedDate, onDateChange, tasks, overallPercent, totalDone }) => {
  return (
    <aside className="card" style={{ minWidth: 200 }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 12 }}>
        <div>
          <div className="muted">Прогресс задач</div>
          <div style={{ fontSize: 20, fontWeight: 700 }}>{overallPercent}%</div>
        </div>
        <div style={{ textAlign: 'right' }}>
          <div className="muted">Выполнено</div>
          <div style={{ fontWeight: 600 }}>{totalDone}</div>
        </div>
      </div>

      <div style={{ marginBottom: 12 }}>
        <div className="muted" style={{ marginBottom: 8 }}>Календарь — нажмите на дату</div>
        <CalendarComponent
          selectedDate={selectedDate}
          onDateChange={onDateChange}
          tasks={tasks}
        />
      </div>

      <div>
        <h4 style={{ margin: '8px 0' }}>Краткая статистика</h4>
        <div style={{ display: 'flex', flexDirection: 'column', gap: 8 }}>
          <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            <span className="muted">Всего задач</span>
            <strong>{tasks.length}</strong>
          </div>
        </div>
      </div>
    </aside>
  );
};

export default Sidebar;
