import React, { useEffect, useState } from 'react';
import { v4 as uuidv4 } from 'uuid';
import { format } from 'date-fns';

// Components
import Header from './components/Header';
import Sidebar from './components/Sidebar';
import TaskForm from './components/TaskForm';
import TaskList from './components/TaskList';
import Charts from './components/Charts';
import HabitForm from './components/HabitForm';
import HabitList from './components/HabitList';
import Settings from './components/Settings';

// Hooks
import { useTheme } from './hooks/useTheme';
import { useData } from './hooks/useData';

// Utils
import { load, save, STORAGE } from './utils/storage';
import { keyOf } from './utils/dateUtils';

function App() {
  // State
  const [tasks, setTasks] = useState(() => load(STORAGE.TASKS, []));
  const [habits, setHabits] = useState(() => load(STORAGE.HABITS, []));
  const [theme, setTheme] = useState(() => load(STORAGE.THEME, 'light'));
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [taskForm, setTaskForm] = useState({ title: '', date: format(new Date(), "yyyy-MM-dd'T'HH:mm") });
  const [habitForm, setHabitForm] = useState({ title: '', cadence: 'daily' });
  const [settingsOpen, setSettingsOpen] = useState(false);

  // Hooks
  useTheme(theme);
  const { completedPerDay, habitCounts, totalDone, totalAll, overallPercent } = useData(tasks, habits);

  // Effects
  useEffect(() => save(STORAGE.THEME, theme), [theme]);
  useEffect(() => save(STORAGE.TASKS, tasks), [tasks]);
  useEffect(() => save(STORAGE.HABITS, habits), [habits]);

  // Task actions
  const addTask = () => {
    if (!taskForm.title.trim()) return;
    
    const datetime = taskForm.date ? new Date(taskForm.date).toISOString() : new Date().toISOString();
    const task = {
      id: uuidv4(),
      title: taskForm.title.trim(),
      datetime,
      completed: false,
      createdAt: new Date().toISOString()
    };
    
    setTasks([task, ...tasks]);
    setTaskForm({ title: '', date: format(new Date(), "yyyy-MM-dd'T'HH:mm") });
  };

  const toggleTask = (id) => {
    setTasks(tasks.map(t => t.id === id ? { ...t, completed: !t.completed } : t));
  };

  const removeTask = (id) => {
    setTasks(tasks.filter(t => t.id !== id));
  };

  const completeAllToday = () => {
    const today = keyOf(new Date());
    setTasks(tasks.map(t => keyOf(t.datetime) === today ? { ...t, completed: true } : t));
  };

  // Habit actions
  const addHabit = () => {
    if (!habitForm.title.trim()) return;
    
    const habit = {
      id: uuidv4(),
      title: habitForm.title.trim(),
      cadence: habitForm.cadence,
      records: {},
      createdAt: new Date().toISOString()
    };
    
    setHabits([habit, ...habits]);
    setHabitForm({ title: '', cadence: 'daily' });
  };

  const toggleHabit = (id, date) => {
    const k = keyOf(date);
    setHabits(habits.map(h => 
      h.id === id 
        ? { 
            ...h, 
            records: { 
              ...(h.records || {}), 
              [k]: h.records && h.records[k] ? undefined : true 
            } 
          } 
        : h
    ));
  };

  const removeHabit = (id) => {
    setHabits(habits.filter(h => h.id !== id));
  };

  // Settings actions
  const fillSample = () => {
    const now = new Date();
    const k = keyOf(now);
    
    setTasks([{
      id: uuidv4(),
      title: 'Прочитать',
      datetime: now.toISOString(),
      completed: false,
      createdAt: now.toISOString()
    }]);
    
    setHabits([{
      id: uuidv4(),
      title: 'Утренняя',
      cadence: 'daily',
      records: { [k]: true },
      createdAt: now.toISOString()
    }]);
  };

  const resetData = () => {
    localStorage.clear();
    window.location.reload();
  };

  // App styles
  const appStyle = {
    background: 'var(--bg)',
    color: 'var(--text)',
    minHeight: '100vh',
    padding: 18,
    boxSizing: 'border-box',
    width: '100%'
  };

  return (
    <div style={appStyle}>
      <div className="container">
        <Header onSettingsClick={() => setSettingsOpen(s => !s)} />

        <Settings
          isOpen={settingsOpen}
          onClose={() => setSettingsOpen(false)}
          theme={theme}
          onThemeChange={setTheme}
          onFillSample={fillSample}
          onReset={resetData}
        />

        <main className="grid">
          <Sidebar
            selectedDate={selectedDate}
            onDateChange={setSelectedDate}
            tasks={tasks}
            overallPercent={overallPercent}
            totalDone={totalDone}
          />

          <section className="card" style={{ minHeight: 320 }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div>
                <h3 style={{ margin: 0 }}>План — {format(selectedDate, 'dd MMM yyyy')}</h3>
                <div className="muted">Добавьте задачу с временем</div>
              </div>

              <div>
                <button className="btn btn-primary" onClick={completeAllToday}>
                  Отметить все
                </button>
              </div>
            </div>

            <TaskForm
              taskForm={taskForm}
              setTaskForm={setTaskForm}
              onSubmit={addTask}
            />

            <TaskList
              tasks={tasks}
              selectedDate={selectedDate}
              onToggleTask={toggleTask}
              onRemoveTask={removeTask}
            />

            <Charts
              completedPerDay={completedPerDay}
              habitCounts={habitCounts}
              overallPercent={overallPercent}
            />
          </section>

          <aside className="card">
            <h3 style={{ margin: 0 }}>Привычки</h3>
            
            <HabitForm
              habitForm={habitForm}
              setHabitForm={setHabitForm}
              onSubmit={addHabit}
            />

            <HabitList
              habits={habits}
              selectedDate={selectedDate}
              onToggleHabit={toggleHabit}
              onRemoveHabit={removeHabit}
              completedPerDay={completedPerDay}
            />
          </aside>
        </main>

        <footer className="muted" style={{ marginTop: 18, textAlign: 'center' }}>
          Данные сохраняются в вашем браузере (localStorage).
        </footer>
      </div>
    </div>
  );
}

export default App;
