import React from 'react';
import { BarChart, Bar, AreaChart, Area, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend } from 'recharts';
import { format, subDays } from 'date-fns';
import { ru } from 'date-fns/locale';

const Charts = ({ completedTasksPerDay, habitCountsPerDay, taskCompletionPercentage }) => {
  // Prepare data for charts
  const taskData = Object.entries(completedTasksPerDay).map(([date, count]) => ({
    date: format(new Date(date), 'dd MMM', { locale: ru }),
    tasks: count,
    fullDate: date
  })).slice(-7);

  const habitData = Object.entries(habitCountsPerDay).map(([date, count]) => ({
    date: format(new Date(date), 'dd MMM', { locale: ru }),
    habits: count,
    fullDate: date
  })).slice(-7);

  const pieData = [
    { name: 'Выполнено', value: taskCompletionPercentage, color: '#10b981' },
    { name: 'Осталось', value: 100 - taskCompletionPercentage, color: '#e5e7eb' }
  ];

  // Custom tooltip for better styling
  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      return (
        <div style={{
          backgroundColor: 'var(--card)',
          border: '1px solid var(--glass-border, rgba(0, 0, 0, 0.1))',
          borderRadius: '12px',
          padding: '12px',
          boxShadow: '0 8px 24px rgba(11, 18, 32, 0.15)',
          backdropFilter: 'blur(10px)',
          fontFamily: 'var(--font-family)'
        }}>
          <p style={{ 
            margin: '0 0 8px 0', 
            fontWeight: '600', 
            color: 'var(--text)',
            fontSize: '0.875rem'
          }}>
            {label}
          </p>
          {payload.map((entry, index) => (
            <p key={index} style={{ 
              margin: '4px 0', 
              color: 'var(--muted)',
              fontSize: '0.8rem'
            }}>
              <span style={{ color: entry.color, fontWeight: '500' }}>
                {entry.name}:
              </span> {entry.value}
            </p>
          ))}
        </div>
      );
    }
    return null;
  };

  return (
    <div style={{ display: 'grid', gap: 16, gridTemplateColumns: '1fr 1fr', marginTop: 16 }}>
      {/* Tasks Chart */}
      <div className="chart-card">
        <div className="chart-title">
          📊 Задачи за неделю
        </div>
        <div className="chart-subtitle">
          Количество выполненных задач по дням
        </div>
        <ResponsiveContainer width="100%" height={200}>
          <BarChart data={taskData} margin={{ top: 5, right: 5, left: 5, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" stroke="var(--input-border)" opacity={0.3} />
            <XAxis 
              dataKey="date" 
              tick={{ fontSize: 12, fill: 'var(--muted)' }}
              axisLine={{ stroke: 'var(--input-border)' }}
            />
            <YAxis 
              tick={{ fontSize: 12, fill: 'var(--muted)' }}
              axisLine={{ stroke: 'var(--input-border)' }}
            />
            <Tooltip content={<CustomTooltip />} />
            <Bar 
              dataKey="tasks" 
              fill="url(#taskGradient)" 
              radius={[4, 4, 0, 0]}
              name="Задачи"
            />
            <defs>
              <linearGradient id="taskGradient" x1="0" y1="0" x2="0" y2="1">
                <stop offset="0%" stopColor="#f59e0b" stopOpacity={0.8} />
                <stop offset="100%" stopColor="#fb7185" stopOpacity={0.6} />
              </linearGradient>
            </defs>
          </BarChart>
        </ResponsiveContainer>
      </div>

      {/* Habits Chart */}
      <div className="chart-card">
        <div className="chart-title">
          📈 Привычки за неделю
        </div>
        <div className="chart-subtitle">
          Активность привычек по дням
        </div>
        <ResponsiveContainer width="100%" height={200}>
          <AreaChart data={habitData} margin={{ top: 5, right: 5, left: 5, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" stroke="var(--input-border)" opacity={0.3} />
            <XAxis 
              dataKey="date" 
              tick={{ fontSize: 12, fill: 'var(--muted)' }}
              axisLine={{ stroke: 'var(--input-border)' }}
            />
            <YAxis 
              tick={{ fontSize: 12, fill: 'var(--muted)' }}
              axisLine={{ stroke: 'var(--input-border)' }}
            />
            <Tooltip content={<CustomTooltip />} />
            <Area 
              type="monotone" 
              dataKey="habits" 
              stroke="#10b981" 
              strokeWidth={3}
              fill="url(#habitGradient)" 
              name="Привычки"
            />
            <defs>
              <linearGradient id="habitGradient" x1="0" y1="0" x2="0" y2="1">
                <stop offset="0%" stopColor="#10b981" stopOpacity={0.3} />
                <stop offset="100%" stopColor="#10b981" stopOpacity={0.1} />
              </linearGradient>
            </defs>
          </AreaChart>
        </ResponsiveContainer>
      </div>

      {/* Overall Progress */}
      <div className="chart-card" style={{ gridColumn: '1 / -1' }}>
        <div className="chart-title">
          🎯 Общий прогресс
        </div>
        <div className="chart-subtitle">
          Процент выполнения всех задач
        </div>
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: 200 }}>
          <ResponsiveContainer width="100%" height="100%">
            <PieChart>
              <Pie
                data={pieData}
                cx="50%"
                cy="50%"
                innerRadius={60}
                outerRadius={80}
                paddingAngle={5}
                dataKey="value"
              >
                {pieData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={entry.color} />
                ))}
              </Pie>
              <Tooltip content={<CustomTooltip />} />
            </PieChart>
          </ResponsiveContainer>
          <div style={{ 
            position: 'absolute', 
            textAlign: 'center',
            pointerEvents: 'none'
          }}>
            <div style={{ 
              fontSize: '2rem', 
              fontWeight: '700', 
              color: 'var(--text)',
              lineHeight: '1'
            }}>
              {taskCompletionPercentage}%
            </div>
            <div style={{ 
              fontSize: '0.875rem', 
              color: 'var(--muted)',
              marginTop: '4px'
            }}>
              выполнено
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Charts;
