import React from 'react';
import ToggleButton from './ToggleButton';

const HabitForm = ({ habitForm, setHabitForm, onSubmit }) => {
  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit();
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
      <div style={{ display: 'flex', gap: '12px', alignItems: 'flex-end' }}>
        <div style={{ flex: 1 }}>
          <label 
            htmlFor="habit-title" 
            style={{ 
              display: 'block', 
              marginBottom: '8px', 
              fontSize: '0.9rem', 
              fontWeight: '500',
              color: 'var(--text)'
            }}
          >
            Название привычки
          </label>
          <input
            id="habit-title"
            type="text"
            value={habitForm.title}
            onChange={(e) => setHabitForm({ ...habitForm, title: e.target.value })}
            placeholder="Например: Утренняя зарядка"
            style={{
              width: '100%',
              padding: '12px 16px',
              border: '1px solid var(--input-border)',
              borderRadius: '12px',
              fontSize: '1rem',
              background: 'var(--input)',
              color: 'var(--text)',
              transition: 'border-color 0.2s ease, box-shadow 0.2s ease'
            }}
            onFocus={(e) => {
              e.target.style.borderColor = 'var(--accent)';
              e.target.style.boxShadow = '0 0 0 3px rgba(var(--accent-rgb), 0.1)';
            }}
            onBlur={(e) => {
              e.target.style.borderColor = 'var(--input-border)';
              e.target.style.boxShadow = 'none';
            }}
          />
        </div>
        
        <div>
          <label 
            htmlFor="habit-cadence" 
            style={{ 
              display: 'block', 
              marginBottom: '8px', 
              fontSize: '0.9rem', 
              fontWeight: '500',
              color: 'var(--text)'
            }}
          >
            Частота
          </label>
          <select
            id="habit-cadence"
            value={habitForm.cadence}
            onChange={(e) => setHabitForm({ ...habitForm, cadence: e.target.value })}
            style={{
              padding: '12px 16px',
              border: '1px solid var(--input-border)',
              borderRadius: '12px',
              fontSize: '1rem',
              background: 'var(--input)',
              color: 'var(--text)',
              cursor: 'pointer',
              transition: 'border-color 0.2s ease, box-shadow 0.2s ease'
            }}
            onFocus={(e) => {
              e.target.style.borderColor = 'var(--accent)';
              e.target.style.boxShadow = '0 0 0 3px rgba(var(--accent-rgb), 0.1)';
            }}
            onBlur={(e) => {
              e.target.style.borderColor = 'var(--input-border)';
              e.target.style.boxShadow = 'none';
            }}
          >
            <option value="daily">Ежедневно</option>
            <option value="weekly">Еженедельно</option>
            <option value="monthly">Ежемесячно</option>
          </select>
        </div>
        
        <ToggleButton
          variant="primary"
          onClick={() => onSubmit()}
          disabled={!habitForm.title.trim()}
          style={{
            padding: '12px 24px',
            borderRadius: '12px',
            fontSize: '1rem',
            fontWeight: '600',
            opacity: habitForm.title.trim() ? 1 : 0.6,
            transition: 'all 0.2s ease'
          }}
        >
          Добавить
        </ToggleButton>
      </div>
    </form>
  );
};

export default HabitForm;
