import React from 'react';
import { ResponsiveContainer, BarChart, Bar, XAxis, Tooltip } from 'recharts';
import { keyOf, getMonthInterval, getDaysDifference } from '../utils/dateUtils';

const HabitList = ({ habits, selectedDate, onToggleHabit, onRemoveHabit, completedPerDay }) => {
  const calculateHabitPercent = (habit) => {
    try {
      const { start, end } = getMonthInterval();
      const days = getDaysDifference(start, end);
      const records = Object.keys(habit.records || {}).filter(k => k >= keyOf(start) && k <= keyOf(end)).length;
      
      if (habit.cadence === 'daily') {
        return Math.min(100, Math.round((records / days) * 100));
      } else {
        return Math.min(100, Math.round((records / Math.ceil(days / 7)) * 100));
      }
    } catch {
      return 0;
    }
  };

  return (
    <div>
      <div style={{ marginTop: 12, display: 'flex', flexDirection: 'column', gap: 10 }}>
        {habits.length === 0 && <div className="muted">Нет привычек — добавьте.</div>}
        
        {habits.map(habit => {
          const percent = calculateHabitPercent(habit);
          const checked = !!(habit.records && habit.records[keyOf(selectedDate)]);
          
          return (
            <div
              key={habit.id}
              style={{
                borderRadius: 12,
                padding: 12,
                border: '1px solid rgba(0,0,0,0.04)'
              }}
            >
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <div style={{ display: 'flex', gap: 10, alignItems: 'center' }}>
                  <input
                    className="checkbox"
                    type="checkbox"
                    checked={checked}
                    onChange={() => onToggleHabit(habit.id, selectedDate)}
                  />
                  <div>
                    <div style={{ fontWeight: 700 }}>{habit.title}</div>
                    <div className="muted">{habit.cadence}</div>
                  </div>
                </div>
                <div style={{ textAlign: 'right' }}>
                  <div style={{ fontWeight: 700 }}>{percent}%</div>
                  <div className="muted">этот месяц</div>
                </div>
              </div>

              <div style={{
                height: 8,
                background: 'rgba(0,0,0,0.04)',
                borderRadius: 8,
                overflow: 'hidden',
                marginTop: 8
              }}>
                <div style={{
                  width: `${percent}%`,
                  height: '100%',
                  background: 'var(--accent)'
                }} />
              </div>

              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: 8 }}>
                <div>
                  <button
                    className="btn"
                    onClick={() => onRemoveHabit(habit.id)}
                    style={{ color: 'crimson' }}
                  >
                    Удалить
                  </button>
                </div>
                <div className="muted">{Object.keys(habit.records || {}).length} отметок</div>
              </div>
            </div>
          );
        })}
      </div>

      <div style={{ marginTop: 14 }}>
        <div className="muted">Активность (7 дней)</div>
        <div style={{ height: 120, marginTop: 8 }}>
          <ResponsiveContainer>
            <BarChart data={completedPerDay} margin={{ top: 6, right: 6, left: 0, bottom: 0 }}>
              <defs>
                <linearGradient id="barG2" x1="0" x2="1">
                  <stop offset="0%" stopColor="var(--accent)" />
                  <stop offset="100%" stopColor="var(--accent-2)" />
                </linearGradient>
              </defs>
              <Bar dataKey="completed" radius={[6, 6, 6, 6]} fill="url(#barG2)" barSize={12} />
              <XAxis dataKey="date" />
              <Tooltip />
            </BarChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
};

export default HabitList;
