import React, { useRef } from 'react';
import { save, STORAGE } from '../utils/storage';

const Settings = ({ isOpen, onClose, theme, onThemeChange, onFillSample, onReset }) => {
  const fileRef = useRef(null);

  const handleFileUpload = (e) => {
    const file = e.target.files && e.target.files[0];
    if (!file) return;
    
    const reader = new FileReader();
    reader.onload = () => {
      try {
        save(STORAGE.BG, reader.result);
        // Reload theme to apply background
        onThemeChange(theme);
      } catch {
        // Silently fail
      }
    };
    reader.readAsDataURL(file);
  };

  const handleUploadClick = () => {
    fileRef.current && fileRef.current.click();
  };

  const cycleTheme = () => {
    const themes = ['light', 'dark', 'glass', 'dark-glass'];
    const currentIndex = themes.indexOf(theme);
    const nextIndex = (currentIndex + 1) % themes.length;
    onThemeChange(themes[nextIndex]);
  };

  if (!isOpen) return null;

  return (
    <>
      <div className="settings-overlay" onClick={onClose} />
      <div className="settings-panel">
        <div className="card">
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 8 }}>
            <strong>Настройки</strong>
            <button className="btn" onClick={onClose}>Закр</button>
          </div>

          <div style={{ display: 'flex', flexDirection: 'column', gap: 10 }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div>
                <div className="muted">Тема</div>
                <div style={{ fontWeight: 700 }}>{theme}</div>
              </div>
              <button
                className="btn"
                onClick={cycleTheme}
              >
                Сменить
              </button>
            </div>

            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div>
                <div className="muted">Демо</div>
                <div style={{ fontWeight: 700 }}>Заполнить пример</div>
              </div>
              <button className="btn btn-primary" onClick={onFillSample}>
                Пример
              </button>
            </div>

            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div>
                <div className="muted">Данные</div>
                <div style={{ fontWeight: 700 }}>Сброс</div>
              </div>
              <button
                className="btn"
                onClick={onReset}
                style={{ color: 'crimson' }}
              >
                Сброс
              </button>
            </div>

            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div className="muted">Фон</div>
              <div style={{ display: 'flex', gap: 8 }}>
                <button className="btn" onClick={handleUploadClick}>
                  Загрузить
                </button>
                <input
                  ref={fileRef}
                  type="file"
                  accept="image/*"
                  style={{ display: 'none' }}
                  onChange={handleFileUpload}
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Settings;
