import React from 'react';
import { BarChart, Bar, AreaChart, Area, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import { format } from 'date-fns';
import { ru } from 'date-fns/locale';

const BottomCharts = ({ completedTasksPerDay, habitCountsPerDay, taskCompletionPercentage }) => {
  // Prepare data for charts
  const taskData = Object.entries(completedTasksPerDay).map(([date, count]) => ({
    date: format(new Date(date), 'dd MMM', { locale: ru }),
    tasks: count,
    fullDate: date
  })).slice(-7);

  const habitData = Object.entries(habitCountsPerDay).map(([date, count]) => ({
    date: format(new Date(date), 'dd MMM', { locale: ru }),
    habits: count,
    fullDate: date
  })).slice(-7);

  const pieData = [
    { name: 'Выполнено', value: taskCompletionPercentage, color: '#10b981' },
    { name: 'Осталось', value: 100 - taskCompletionPercentage, color: '#e5e7eb' }
  ];

  // Custom tooltip for better styling
  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      return (
        <div style={{
          backgroundColor: 'var(--card)',
          border: '1px solid var(--glass-border, rgba(0, 0, 0, 0.1))',
          borderRadius: '8px',
          padding: '8px',
          boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
          backdropFilter: 'blur(10px)',
          fontFamily: 'var(--font-family)',
          fontSize: '0.75rem'
        }}>
          <p style={{ 
            margin: '0 0 4px 0', 
            fontWeight: '600', 
            color: 'var(--text)',
            fontSize: '0.75rem'
          }}>
            {label}
          </p>
          {payload.map((entry, index) => (
            <p key={index} style={{ 
              margin: '2px 0', 
              color: 'var(--muted)',
              fontSize: '0.7rem'
            }}>
              <span style={{ color: entry.color, fontWeight: '500' }}>
                {entry.name}:
              </span> {entry.value}
            </p>
          ))}
        </div>
      );
    }
    return null;
  };

  return (
    <div className="bottom-charts-container">
      <div className="bottom-charts-wrapper">
        {/* Tasks Chart */}
        <div className="bottom-chart-card">
          <div className="bottom-chart-title">📊 Задачи</div>
          <ResponsiveContainer width="100%" height={120}>
            <BarChart data={taskData} margin={{ top: 5, right: 5, left: 5, bottom: 5 }}>
              <CartesianGrid strokeDasharray="3 3" stroke="var(--input-border)" opacity={0.3} />
              <XAxis 
                dataKey="date" 
                tick={{ fontSize: 10, fill: 'var(--muted)' }}
                axisLine={{ stroke: 'var(--input-border)' }}
              />
              <YAxis 
                tick={{ fontSize: 10, fill: 'var(--muted)' }}
                axisLine={{ stroke: 'var(--input-border)' }}
              />
              <Tooltip content={<CustomTooltip />} />
              <Bar 
                dataKey="tasks" 
                fill="var(--accent)" 
                radius={[2, 2, 0, 0]}
                name="Задачи"
              />
            </BarChart>
          </ResponsiveContainer>
        </div>

        {/* Habits Chart */}
        <div className="bottom-chart-card">
          <div className="bottom-chart-title">📈 Привычки</div>
          <ResponsiveContainer width="100%" height={120}>
            <AreaChart data={habitData} margin={{ top: 5, right: 5, left: 5, bottom: 5 }}>
              <CartesianGrid strokeDasharray="3 3" stroke="var(--input-border)" opacity={0.3} />
              <XAxis 
                dataKey="date" 
                tick={{ fontSize: 10, fill: 'var(--muted)' }}
                axisLine={{ stroke: 'var(--input-border)' }}
              />
              <YAxis 
                tick={{ fontSize: 10, fill: 'var(--muted)' }}
                axisLine={{ stroke: 'var(--input-border)' }}
              />
              <Tooltip content={<CustomTooltip />} />
              <Area 
                type="monotone" 
                dataKey="habits" 
                stroke="var(--green)" 
                strokeWidth={2}
                fill="var(--green)" 
                fillOpacity={0.3}
                name="Привычки"
              />
            </AreaChart>
          </ResponsiveContainer>
        </div>

        {/* Overall Progress */}
        <div className="bottom-chart-card">
          <div className="bottom-chart-title">🎯 Прогресс</div>
          <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: 120, position: 'relative' }}>
            <ResponsiveContainer width="100%" height="100%">
              <PieChart>
                <Pie
                  data={pieData}
                  cx="50%"
                  cy="50%"
                  innerRadius={30}
                  outerRadius={45}
                  paddingAngle={2}
                  dataKey="value"
                >
                  {pieData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={entry.color} />
                  ))}
                </Pie>
                <Tooltip content={<CustomTooltip />} />
              </PieChart>
            </ResponsiveContainer>
            <div style={{ 
              position: 'absolute', 
              textAlign: 'center',
              pointerEvents: 'none'
            }}>
              <div style={{ 
                fontSize: '1.2rem', 
                fontWeight: '700', 
                color: 'var(--text)',
                lineHeight: '1'
              }}>
                {taskCompletionPercentage}%
              </div>
              <div style={{ 
                fontSize: '0.6rem', 
                color: 'var(--muted)',
                marginTop: '2px'
              }}>
                готово
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BottomCharts;
